import "./project.css";
import ProjectData from "./ProjectData";
import { useState } from "react";

function Project() {

    const [btnActive1, setBtnActive1] = useState("active");
    const [btnActive2, setBtnActive2] = useState("");
    const [btnActive3, setBtnActive3] = useState("");
    const [btnActive4, setBtnActive4] = useState("");
    const [btnActive5, setBtnActive5] = useState("");

const data = [
    {
        id:1,
        uniqueData:1,
        headerTitle:"Project Title1",
        tooTipTitle:"Tool tip Title",
        imgUrl:"assets/images/n1.jpg",
        projectText:"Project Decription",

    },
    {
        id:2,
        uniqueData:1,
        headerTitle:"Project Title2",
        tooTipTitle:"Tool tip Title",
        imgUrl:"assets/images/n1.jpg",
        projectText:"Project Decription",

    },
    {
        id:3,
        uniqueData:4,
        headerTitle:"Project Title3",
        tooTipTitle:"Tool tip Title",
        imgUrl:"assets/images/n1.jpg",
        projectText:"Project Decription",

    },
    {
        id:4,
        uniqueData:3,
        headerTitle:"Project Title4",
        tooTipTitle:"Tool tip Title",
        imgUrl:"assets/images/n1.jpg",
        projectText:"Project Decription",

    },
    {
        id:5,
        uniqueData:2,
        headerTitle:"Project Title5",
        tooTipTitle:"Tool tip Title",
        imgUrl:"assets/images/n1.jpg",
        projectText:"Project Decription",

    },
    {
        id:2,
        uniqueData:2,
        headerTitle:"Project Title5",
        tooTipTitle:"Tool tip Title",
        imgUrl:"assets/images/n1.jpg",
        projectText:"Project Decription",

    },
    {
        id:2,
        uniqueData:5,
        headerTitle:"Project Title5",
        tooTipTitle:"Tool tip Title",
        imgUrl:"assets/images/n1.jpg",
        projectText:"Project Decription",

    },
]

const [myID, setMyID] = useState(1);

var filterDataHandler1 = () =>{
    setBtnActive1("active");
    setBtnActive2("");
    setBtnActive3("");
    setBtnActive4("");
    setBtnActive5("");

    setMyID(1);
}
var filterDataHandler2 = () =>{
    setBtnActive2("active");
    setBtnActive1("");
    setBtnActive3("");
    setBtnActive4("");
    setBtnActive5("");

    // data.map((newData) =>{
        // if(newData.id === 2)
        // {
        //     console.log(newData);
        //     setData(newData);
        // }
    // })
    setMyID(2);
}
var filterDataHandler3 = () =>{
    setBtnActive3("active");
    setBtnActive2("");
    setBtnActive1("");
    setBtnActive4("");
    setBtnActive5("");

    setMyID(3);
}
var filterDataHandler4 = () =>{
    setBtnActive4("active");
    setBtnActive2("");
    setBtnActive3("");
    setBtnActive1("");
    setBtnActive5("");

    setMyID(4);
}
var filterDataHandler5 = () =>{
    setBtnActive5("active");
    setBtnActive2("");
    setBtnActive3("");
    setBtnActive4("");
    setBtnActive1("");

    setMyID(5);
}

  return (
    <div className="container" style={{ marginTop: "60px" }}>

        <div className="myBtnContainer text-center">   
          <button className={`btn p-2 m-4 ${btnActive1}`} onClick={filterDataHandler1}>
            {" "}
            Show all
          </button>
          <button className={`btn p-2 m-4 ${btnActive2}`} onClick={filterDataHandler2}>
            {" "}
            Django
          </button>
          <button className={`btn p-2 m-4 ${btnActive3}`} onClick={filterDataHandler3}>
            {" "}
            Django Rest API
          </button>
          <button className={`btn p-2 m-4 ${btnActive4}`} onClick={filterDataHandler4}>
            {" "}
            React JS
          </button>
          <button className={`btn p-2 m-4 ${btnActive5}`} onClick={filterDataHandler5}>
            {" "}
            Others
          </button>
        </div>

        <div className="row mt-5 text-center projectItems">
            {
                data.filter(items => items.uniqueData === myID).map(item => (

                    <ProjectData data={item} key={item.id}/>

                ))
                // data.map((dataDetails) => {

                //     return(

                //         <ProjectData data={dataDetails} key={dataDetails.id}/>
                //     )
                // })
            }

        </div>

    </div>
  );
}

export default Project;
