import { ArrowDownward } from "@material-ui/icons";
import React from "react";
import "./certificate.css";

function Certificate() {
  const certificates = [
    {
      id:1,
      imgUrl:"assets/Certificate/Coursera Certificate.pdf",
    },
    {
      id:2,
      imgUrl:"assets/language-images/dj.png",
    },
    {
      id:3,
      imgUrl:"assets/language-images/drf.png",
    },
    {
      id:4,
      imgUrl:"assets/language-images/react.png",
    },
    {
      id:5,
      imgUrl:"assets/language-images/php.png",
    },
    {
      id:6,
      imgUrl:"assets/language-images/java2.png",
    },
  ]
  return (
    <div className="container" style={{ marginTop: "60px" }}>
      <p className="text-center contText mt-5">Certificates</p>
      <br />
      <p className="contp mb-4">
        Here my Certificates that I avhieved. <ArrowDownward />
      </p>

      <div className="row techRow">
        {
            certificates.map((certi) => {
              return(
                <div className="col-6 certiDiv">
                {" "}
                <a href={certi.imgUrl} target={"_blank"}>
                  <img
                    src={certi.imgUrl}
                    alt=""
                    className="certiImg"
                  />
                  <div className="overlay">
                    <div className="certiBtnText">Click to show me</div>
                  </div>
                </a>{" "}
              </div>
              )
            })
        }

      </div>
    </div>
  );
}

export default Certificate;
