    <div className="container" style={{ marginTop: "60px" }}>

        <div className="myBtnContainer text-center">   
          <button className="btn active p-2 m-4" onClick={filterDataHandler}>
            {" "}
            Show all
          </button>
          <button className={`btn p-2 m-4 ${btnActive}`} onClick={filterDataHandler}>
            {" "}
